    private void ResolverActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ResolverActionPerformed
      Sudoku1 s=new Sudoku1();
 try{
      int [][]A={
        {Integer.parseInt(pos1.getText()), Integer.parseInt(pos2.getText()), Integer.parseInt(pos3.getText()), Integer.parseInt(pos4.getText())},
        {Integer.parseInt(pos10.getText()), Integer.parseInt(pos11.getText()), Integer.parseInt(pos12.getText()), Integer.parseInt(pos13.getText())},
        {Integer.parseInt(pos19.getText()), Integer.parseInt(pos20.getText()), Integer.parseInt(pos21.getText()), Integer.parseInt(pos22.getText()), },
        {Integer.parseInt(pos28.getText()), Integer.parseInt(pos29.getText()), Integer.parseInt(pos30.getText()), Integer.parseInt(pos31.getText())},
      
      };
 }catch(NumberFormatException e){
     mensaje.showMessageDialog(Resolver,"Error no ingresar texto",null,mensaje.ERROR_MESSAGE);
 }
 int [][]A={
        {Integer.parseInt(pos1.getText()), Integer.parseInt(pos2.getText()), Integer.parseInt(pos3.getText()), Integer.parseInt(pos4.getText())},
        {Integer.parseInt(pos10.getText()), Integer.parseInt(pos11.getText()), Integer.parseInt(pos12.getText()), Integer.parseInt(pos13.getText())},
        {Integer.parseInt(pos19.getText()), Integer.parseInt(pos20.getText()), Integer.parseInt(pos21.getText()), Integer.parseInt(pos22.getText()), },
        {Integer.parseInt(pos28.getText()), Integer.parseInt(pos29.getText()), Integer.parseInt(pos30.getText()), Integer.parseInt(pos31.getText())},
      
      };
     
      if(s.ValidaInicio(A)==false)
          mensaje.showMessageDialog(Resolver,"Error. Ponga n√∫meros dentro de rango.",null,mensaje.ERROR_MESSAGE);
      else     
          
      s.llena(A, 0, 0, 1);
      pos1.setText(A[0][0]+"");
      pos2.setText(A[0][1]+"");
      pos3.setText(A[0][2]+"");
      pos4.setText(A[0][3]+""); 
      pos10.setText(A[1][0]+"");
      pos11.setText(A[1][1]+"");
      pos12.setText(A[1][2]+"");
      pos13.setText(A[1][3]+"");
      pos19.setText(A[2][0]+"");
      pos20.setText(A[2][1]+"");
      pos21.setText(A[2][2]+"");
      pos22.setText(A[2][3]+"");
      pos28.setText(A[3][0]+"");
      pos29.setText(A[3][1]+"");
      pos30.setText(A[3][2]+"");
      pos31.setText(A[3][3]+"");
      
            
    }//GEN-LAST:event_ResolverActionPerformed

